var mapHashmap=function(a,b){var c={};for(var d in a){var e=a[d];c[d]=b(e,d)}return c},mapArray=function(a,b){for(var c=[],d=0;d<a.length;++d){var e=a[d];c[d]=b(e,d)}return c};exports.map=function(a){return Array.isArray(a)?mapArray.apply(this,arguments):mapHashmap.apply(this,arguments)},exports.reduce=function(a,b,c){for(var d in a){var e=a[d];c=b(c,e,d)}return c},exports.snakeCase=function(a){return a.replace(/([a-z])([A-Z])/,function(a,b,c){return b+"_"+c.toLowerCase()}).toLowerCase()};